import { useEffect, useState } from "react";
import { Dateformatter } from "../../../utils/dateChanger";
import dayjs from "dayjs";
import apifunction from "../../../utils/apiCall";
import { api } from "../../../constant/apiConstant";
import { DatePicker } from "antd";
import "./index.scss";
import { FeedbackInfoCard } from "../../../type";
import FeedbackInfo from "../../../component/feedbackInfoCard";
import { FaUserCircle } from "react-icons/fa";
import { CiStar } from "react-icons/ci";
<StarOutlined />

const Feedbackpage = () => {
  const [date, setDate] = useState(Dateformatter(dayjs(new Date()), true));
  const [feedback, setFeedback] = useState<FeedbackInfoCard[]>([]);
  const [activeclass, setActiveclass] = useState(0);
  const [activeinfo, setActiveInfo] = useState<FeedbackInfoCard>({
    hostname: "",
    feedback: "",
    rating:0,
    meetingId: 154,
    timestamp: "",
    seen: false,
  });
  const feebackLength = Array.from({ length: 5 });
  useEffect(() => {
    fetchFeedback(date);
    // eslint-disable-next-line
  }, []);

  const data = [
    {
      hostname: "Ragul Seeman magotira",
      feedback:
        "123ertyuiolkbvcasxcfvgbnjmk,xdcf vgbhnjmkzxdcfvgbhnjmkzxdcfvg bhnjmzxdcfvgbnazwsxed rcftvgbhnjmzxdcfvgbhnjmawzs xdcfvgbhnjmawze sxdrcftvgzasxdcfvgybhnze xrdctfvgybuhnzexr ctfvygb",
      rating: 3,
      meetingId: 154,
      timestamp: "2024-03-09T08:57:53.181Z",
      seen: false,
    },
    {
      hostname: "ragul",
      feedback: "4rsryyu",
      rating: 4,
      meetingId: 154,
      timestamp: "2024-03-09T08:57:53.181Z",
      seen: true,
    },
    {
      hostname: "ragul",
      feedback: "rtyuiopv",
      rating: 4,
      meetingId: 154,
      timestamp: "2024-03-09T09:45:10.791Z",
      seen: false,
    },
    {
      hostname: "ragul",
      feedback: "ertyuiolkvc",
      rating: 4,
      meetingId: 154,
      timestamp: "2024-03-08T08:57:53.181Z",
      seen: false,
    },
  ];
  const fetchFeedback = async (date: string) => {
    setDate(date);
    // const response = await apifunction({
    //   url: api.baseurl + api.adminFeedback + date,
    //   method: "get",
    //   auth: true,
    // }).then((res) => {
    //   return res.data.data as FeedbackInfoCard[];
    // });
    await setFeedback(data);
    if (data.length > 0) {
      await setActiveInfo(data[0]);
    }
  };
  const checkRating=(index:number)=>{
if(activeinfo.rating>=index){
  return true;
}else{
  return false;
}
  }
  return (
    <div className="feeback-container">
      <h2>Feedback</h2>
      <div className="main-container">
        <section className="left-container">
          <DatePicker
            onChange={(e: any) => {
              fetchFeedback(Dateformatter(e.$d, true));
            }}
            value={dayjs(date)}
            format={"DD/MM/YYYY"}
            allowClear={false}
            className="datepicker"
          />
          <div className="list-of-feedback">
            <h3>New Notification</h3>
            {feedback.map((item, index) => {
              return (
                <FeedbackInfo
                  data={item}
                  key={index}
                  active={activeclass === index ? true : false}
                  callback={(data: FeedbackInfoCard) => {
                    setActiveclass(index);
                    setActiveInfo(data);
                  }}
                />
              );
            })}
          </div>
        </section>
        <section className="right-container">
          <header className="right-header">
            {" "}
            <FaUserCircle size={"80px"} /> <h2>{activeinfo.hostname}</h2>
          </header>
          <p>{activeinfo.feedback}</p>
          <footer>{feebackLength.map((_,index)=>{
            return (<CiStar size={40} className={checkRating(index+1)?"Yellow":""}/>)
          })}</footer>
        </section>
      </div>
    </div>
  );
};

export default Feedbackpage;
