import { Button,Modal } from 'antd';
import {icons} from '../../constant/reacticonConstant'
import { CSSProperties, useEffect, useState } from 'react';
import {blockedactiontype}from '../../type/index';

const closebuttonstyle:CSSProperties={
    backgroundColor:'#5473E3',marginLeft:'11rem',width:'8rem',height:'3rem',
    textAlign:'center',color:'white',fontSize:'1.3rem',cursor:'ponter',
};
const iconstyle:CSSProperties={ fontSize:'4rem',color:'#5473E3',marginLeft:'12.8rem'};
const parastyle:CSSProperties={ textAlign:'center',fontSize:'1.5rem',fontWeight:'medium',padding:'1rem'};
const modelStyle:CSSProperties={height:'15rem'};

const BlockVerification= ({Isblocked,setIsblocked}:blockedactiontype) => {

  const [block,setblock]=useState({status:''});

  const checkblock={
    success:{
      status:'Slot Blocked',
    },
    failure:{
     status:'Slot Unavailable',
    },
 };

 useEffect(()=>{
   if(Isblocked.message==='block'){
    setblock(checkblock.success);
   }else if(Isblocked.message==='unblock'){
     setblock(checkblock.failure);
   }
   // eslint-disable-next-line
},[Isblocked])


 const handleOk = () => {
  setIsblocked(false);
};
  const handleCancel = () => {
    setIsblocked(false);
  };

  return (
    <>
      <Modal footer={null} style={modelStyle} open={Isblocked.open} centered onCancel={handleCancel}>
        <icons.GiConfirmed style={iconstyle}/>
        <p style={parastyle}>{block.status}</p>
        <Button style={closebuttonstyle} onClick={handleOk}>Close</Button>
      </Modal>
    </>
  );
};

export default BlockVerification;
