import { useState } from "react";
import { ActivityCardentity } from "../../type";
import { CloseOutlined } from "@ant-design/icons";
import { FaUserCircle } from "react-icons/fa";

import "./index.scss";
type Props = {
  data: ActivityCardentity;
  callback: any;
};

const Viewcard = (props: Props) => {
  const [mysection, setMysection] = useState("Summary");
  const { data, callback } = props;
  const section = [
    "Summary",
    "Attendies",
    "Agenda",
    data.reason ? "Reason" : "",
  ];
  const reasonStyle={width:"500px"}
  const normalStyle={width:"400px"}
  return (
    <div className="View-outerlayer">
      <section className={"inner-layer"} style={data.reason?reasonStyle:normalStyle}>
        <header>
          <ul>
            {section.map((item) => {
              return (
                <li
                  key={item}
                  className={
                    mysection === item ? "view-header" : "view-nonheader"
                  }
                  onClick={() => {
                    setMysection(item);
                  }}
                >
                  {item}
                </li>
              );
            })}
          </ul>{" "}
          <CloseOutlined
            onClick={() => {
              callback();
            }}
          />
        </header>

        {mysection === "Summary" ? (
          <div className="summary">
            <label>
              Meeting Name :<span>{data.meetingName}</span>{" "}
            </label>
            <label>
              Meeting Type :<span>{data.meetingCategory}</span>
            </label>
            <label>
              Date :<span>{data.startDate}</span>
            </label>
            <label>
              Time :
              <span>
                {data.startTime} to {data.endTime}
              </span>
            </label>
            <label>
              Venue :<span>{data.roomName}</span>
            </label>
          </div>
        ) : (
          <></>
        )}

        {mysection === "Attendies" ? (
          <section className="list-of-attendies">
            {" "}
            {data.guestList.map((item, index) => {
              return (
                <span key={index} className="attendy">
                  <FaUserCircle />
                  {item.name}
                </span>
              );
            })}
          </section>
        ) : (
          <></>
        )}
        {mysection === "Agenda" ? (
          <section className="agenda">
            <p className="agenda-des" style={data.reason?reasonStyle:normalStyle}>{data.description}</p>
          </section>
        ) : (
          <></>
        )}
        {mysection === "Reason" ? (
          <section className="agenda">
            <p style={data.reason?reasonStyle:normalStyle} className="agenda-des">{data.reason}</p>
          </section>
        ) : (
          <></>
        )}
      </section>
    </div>
  );
};
export default Viewcard;
