flexDirection: 'column',
gap: '1rem',
maxHeight: '300px',
overflowY: 'hidden',
};

export const ebHeaderStyle: CSSProperties = {
lineHeight: '1.5rem',
padding: '0.2rem',
};

export const ebItemBoxStyle: CSSProperties = {
display: 'flex',
flexDirection: 'column',
overflowY: 'scroll',
};

export const checkboxItemBoxStyle: CSSProperties = {
display: 'flex',
flexDirection: 'row',
gap: '5px',
};

export const profileIconStyle: CSSProperties = {
fontSize: '50px',
};

export const bookingstatus_box: CSSProperties = {
display: 'flex',
flexDirection: 'column',
alignItems: 'center',
justifyContent: 'center',
gap: '1rem',
fontSize: 30,
};
export const bb_info: CSSProperties = {
fontSize: 20,
};
export const button: CSSProperties = {
padding: '0.7rem',
width: '5rem',
