import { Dayjs } from 'dayjs';
import dayjs from 'dayjs';
export const rangeDisabler=(date:Dayjs,pickerdate:Dayjs)=>{
  const currentDate =dayjs();
  const currentTime = dayjs();
    if (currentDate.isSame(pickerdate, "day")) {
      const currentHour = currentTime.hour();
      const currentMinute = currentTime.minute();
      const isFinishedHour =
        date === currentDate && date.hour() < currentHour;
      if (isFinishedHour) {
        return {
          disabledHours: () =>
            Array.from({ length: currentHour + 1 }, (_, i) => i),
          disabledMinutes: () =>
            Array.from({ length: 60 }, (_, i) => i),
        };
      } else if (date.hour() === currentHour) {
        return {
          disabledHours: () =>
            Array.from({ length: currentHour }, (_, i) => i),
          disabledMinutes: () =>
            Array.from({ length: currentMinute + 11 }, (_, i) => i),
        };
      } else if (date.hour() > currentHour) {
        return {
          disabledHours: () =>
            currentTime && currentDate.isSame(date, "day")
              ? Array.from(
                  { length: currentTime.hour() },
                  (_, i) => i
                )
              : [],
        };
      }
    }
    return {
      disabledHours: () => [],
      disabledMinutes: () => [],
    };
  }
