import { Modal, DatePicker, Button, Form, Input } from 'antd';
import { useState } from 'react';
import { CSSProperties } from 'react';
import dayjs from 'dayjs';
import { Dayjs } from 'dayjs';
import { formatTimeTo24Hours } from '../../utils/function';
import { BlockformProps } from '../../type/index';
import { rangeDisabler } from '../../utils/dateDisabler';

const astriex: CSSProperties = { color: 'red' };
const titlestyle: CSSProperties = { fontSize: '2rem', fontWeight: 'medium', color: 'grey' };
const fromTime: CSSProperties = { display: 'inline-block', margin: '0rem 5rem 0rem 0rem' };
const Blockbutton: CSSProperties = { textAlign: 'center' };
const buttonstyle: CSSProperties = { width: '7rem', padding: '0rem 1.5rem', fontWeight: 'bolder', height: '3rem', cursor: 'pointer' };
const timewidth: CSSProperties = { width: '12rem' };
const datestyle: CSSProperties = { margin: '1rem 0rem 0rem 0rem' };

const Blockform = ({ IsModalOpen, setIsModalOpen, roomname, date, meetingblock, settimedayjs, timedayjs, setmeetingblock, settime, onblock, book }: BlockformProps) => {
  const { TextArea } = Input;
  const datevalue=date;
  const handleInputChange = (value: string) => {
    setmeetingblock(prevState => ({
      ...prevState,
      description: value,
    }));
  };

  const onTimeChange = (values: any[]) => {
    if (values && values.length === 2) {
      const [startTime, endTime] = values;
      settimedayjs(prevState => ({
        ...prevState,
        startdayjs: startTime,
        enddayjs: endTime,
      }));
      settime(prevState => ({
        ...prevState,
        starttime: formatTimeTo24Hours(startTime),
        endtime: formatTimeTo24Hours(endTime),
      }));
    }
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  // eslint-disable-next-line
  const [currentDate, setCurrentDate] = useState(dayjs());
  // eslint-disable-next-line
  const [currentTime, setCurrentTime] = useState(dayjs());

  return (
    <Modal title={<span style={titlestyle}>{roomname}</span>} footer={null} open={IsModalOpen} onCancel={handleCancel}>
      <Form layout='vertical'>
        <Form.Item
          label={<span style={datestyle}>Date <span style={astriex}>*</span></span>}
        >
          <DatePicker value={datevalue} format='DD-MM-YYYY' style={timewidth} disabled inputReadOnly />
        </Form.Item>
        <Form.Item label={<span>From <span style={astriex}>*</span></span>} style={fromTime}>
          <DatePicker.RangePicker
            picker="time"
            format='hh:mm a'
            style={{ width: '100%' }}
            value={[timedayjs.startdayjs, timedayjs.enddayjs]}
            onChange={onTimeChange}
            disabledTime={(date: Dayjs) => rangeDisabler(date,datevalue)}
          />
        </Form.Item>
        <Form.Item label={<span>Reason <span style={astriex}>*</span></span>}>
          <TextArea value={meetingblock.description} rows={4} onChange={(e) => handleInputChange(e.target.value)} />
        </Form.Item>
        <Form.Item style={Blockbutton}>
          <Button disabled={!book} type="primary" style={buttonstyle} onClick={onblock}>
            Block
          </Button>
        </Form.Item>
      </Form>
    </Modal>
  )
}

export default Blockform;
