import { Modal } from 'antd';
import { Button, Form, DatePicker,Input,TimePicker} from 'antd';
import { CSSProperties } from 'react';
import  { Dayjs } from 'dayjs';
import {formatTimeTo24Hours} from '../../../../utils/function'
import {BlockformProps} from '../../../../type/index'

const astriex: CSSProperties = {color:'red'}; 
const titlestyle:CSSProperties={fontSize:'2rem',fontWeight:'medium',color:'grey'}
const fromTime:CSSProperties={display:'inline-block',margin:'0rem 5rem 0rem 0rem'};
const toTime:CSSProperties={display:'inline-block'};
const Blockbutton:CSSProperties={textAlign:'center'};
const buttonstyle:CSSProperties={width:'7rem',padding:'0rem 1.5rem',fontWeight:'bolder',height:'3rem'};
const timewidth:CSSProperties={width:'12rem'};
const datestyle:CSSProperties={margin:'1rem 0rem 0rem 0rem'};

const Blockform= ({IsModalOpen,setIsModalOpen,roomname,date,
  meetingblock,settimedayjs,timedayjs,
  setmeetingblock,settime,onblock,book}:BlockformProps) => {

  const { TextArea } = Input;

    const handleInputChange = (value: string) => {
      setmeetingblock(prevState => ({
        ...prevState,
        description:value,
      }));
    };



  const onStartTime = (time: Dayjs) => {
    if (time) {
      settimedayjs(pre=>({
        ...pre,
        startdayjs:time,
      }))
      settime(prevState => ({
        ...prevState,
        starttime:formatTimeTo24Hours(time),
      }))
    }
  };

  const onEndTime = (time: Dayjs) => {
    if (time) {
      settimedayjs(pre=>({
        ...pre,
        enddayjs:time,
      }))
      settime(prevState => ({
        ...prevState,
        endtime:formatTimeTo24Hours(time),
      }))
    }
  };
  const handleCancel = () => {
        setIsModalOpen(false);
  };
  return (
    <Modal title={<span style={titlestyle}>{roomname}</span>}footer={null} open={IsModalOpen} onCancel={handleCancel}>
    <Form layout='vertical'>
    <Form.Item 
      label={<span style={datestyle}>Date <span style={astriex}>*</span></span>}
    >
    <DatePicker value={date} format='DD-MM-YYYY' style={timewidth} disabled inputReadOnly/>
    </Form.Item>
    <Form.Item label={<span>From <span style={astriex}>*</span></span>}style={fromTime}>
          <TimePicker value={timedayjs.startdayjs} className='time' format='h:mm A'  onChange={onStartTime} />
    </Form.Item>
    <Form.Item  label={<span>To <span style={astriex}>*</span></span>}style={toTime}>
          <TimePicker value={timedayjs.enddayjs} className='time' format='h:mm A'  onChange={onEndTime} />
    </Form.Item>
    <Form.Item label={<span>Reason <span style={astriex}>*</span></span>}>
          <TextArea value={meetingblock.description} rows={4} onChange={(e)=>handleInputChange(e.target.value)} />
    </Form.Item>
    <Form.Item style={Blockbutton}>
        <Button disabled={!book} type="primary" style={buttonstyle} onClick={onblock}>
         Block
        </Button>
    </Form.Item>
    </Form>
  </Modal>
  )
}

export default Blockform
