import { useEffect, useState } from 'react';
import apifunction from '../../utils/apiCall';
import { api } from '../../constant/apiConstant';
import Amenities from '../../component/amenities';
import Roomabout from '../../component/roomabout';
import Roomform from '../../component/bookingroomform';
import Roomimage from '../../component/roomimage';
import Navback from '../../component/navbackbutton';
import { EmploeenameidType, MeetCategoryType, RoomData, cons } from '../../type';
import {confirbooking} from '../../constant/confirbookingConstant';

//stylesheet
import './index.scss';
const BookingdetailPage= () => {

  const [roomData, setRoomData] = useState<RoomData>({} as RoomData);
  const [meetcategory,setmeetcategory]=useState<MeetCategoryType>({} as MeetCategoryType);
  const [empnameid,setempnameid]=useState<EmploeenameidType>({} as EmploeenameidType);
  const [bookstatus,setbookstatus]=useState<cons>(confirbooking.failed);
  const id:number=4;
  const fetchcategory = async () => {
    try {
      const meetcat = await apifunction({
        url: `${api.baseurl}${api.meetingcategoryurl}`,
        method: 'get',
        auth: true,
      });
      const empnameid=await apifunction({
        url:api.baseurl + api.employenameid,
        method:'get',
        auth:true,
      });
      setempnameid(empnameid.data.data);
      setmeetcategory(meetcat.data.data);
    } catch (error) {
      console.error('Error fetching meeting categories:', error);
    }
  };

  const fetchData = async (id:number) => {
    try {
      const response = await apifunction({
        url: `${api.baseurl}${api.bookingroomdetailbyid}${id}`,
        method: 'get',
        auth: true,
      });
      setRoomData(response.data.data);
    } catch (error) {
      console.error('Error fetching room data:', error);
    }
  };

  const putbookroomdata=async(data:any)=>{
   try{
    console.log(data);
    const res=await apifunction({
      url:api.baseurl + api.posttbookingroomform,
      method:'post',
      auth:true,
      payload:data,
    });
    if (res.status===200 || res.status===201) {
      setbookstatus(confirbooking.success);
    }
   }catch(error){
    setbookstatus(confirbooking.failed);
    console.error('Error in Booking room',error);
   }
  }

  useEffect(() => {
    fetchData(id);
    fetchcategory();
  }, []);

  return (
    <div className='Meeting_page'>
      <Navback/>
      <div className='Meetingimageform_container'>
        <Roomimage imageUrl={roomData?.imageUrl}/>
        <Roomform roomData={roomData} cons={bookstatus} category={meetcategory} empnameid={empnameid} callbackbody={(data:any)=>putbookroomdata(data)}/>
      </div>
      <div className='About'>
        <h3>About</h3>
        <Roomabout/>
      </div>
      <div className='Amenities'>
        <h3>Amenities</h3>
        <Amenities minCapacity={roomData?.minCapacity} maxCapacity={roomData?.maxCapacity} facilitiesEntityList={roomData?.facilitiesEntityList}/>
      </div>
    </div>
  )
}

export default BookingdetailPage
