import { useState } from "react";
import { api } from "../../constant/apiConstant";
import { ActivityCardentity } from "../../type";
import apifunction from "../../utils/apiCall";
import Rejectbutton from "../buttons/confirmationButton";
import "./index.scss";
import {
  ClockCircleTwoTone,
  EditTwoTone,
  EyeTwoTone,
  UserOutlined,
  CalendarTwoTone,
} from "@ant-design/icons";
import Popcard from "../../models/activityConfirmationpopup";
import Viewcard from "../../models/activityViewdetails";
import { useNavigate } from "react-router-dom";

type Props = {
  isedit: boolean;
  isview: boolean;
  iscancel: boolean;
  ishost: boolean;
  data: ActivityCardentity;
  divison?: string;
  callback: any;
  isLoading:any;
};

const Card = (props: Props) => {
  const { isedit, isview, data, divison, iscancel, ishost, callback,isLoading } = props;
  const [pop, setPop] = useState(false);
  const [view, setView] = useState(false);
  const [card, setCarddata] = useState<ActivityCardentity>();
  const navigate=useNavigate();

  const widthdrawcall = (id: number) => {
    if (id) {
      setPop(true);
    }
  };
  const completewithdraw = async (type: boolean) => {
    setPop(false);
    isLoading(true);
    if (type) {
     await apifunction({
        url: api.baseurl + api.userPendingwithdraw + data.id,
        method: "put",
        auth: true,
      });

      if (divison === "Pending") {
        callback("reloadPending");
      } else {
        callback("reloadUpcoming");
      }
    } else {
      setPop(false);
    }
  };
  const myViewcard = (mydata: ActivityCardentity) => {
    setCarddata(mydata);
    setView(true);
  };
const handleNavigate=(obj:ActivityCardentity)=>{
  navigate("/user/bookform",{state:obj});
}
  return (
    <div className="acitivity-card">
      {view && (
        <Viewcard
          data={card as ActivityCardentity}
          callback={() => {
            setView(false);
          }}
          isopen={view}
        />
      )}
      {pop && (
        <Popcard
          message={
            divison === "Pending"
              ? "Are you sure you want to Withdraw the meeting?"
              : "Are you sure you want to cancel the meeting?"
          }
          okay={"confirm"}
          reject={"cancel"}
          callback={(e: string) => {
            e === "confirm" ? completewithdraw(true) : completewithdraw(false);
          }}
        />
      )}

      <p className="meeting-name">
        {data.meetingName}({data.meetingCategory})
      </p>
      {ishost && (
        <span className="host">
          <UserOutlined />
          {data.hostName}
        </span>
      )}
      <span className="indicator">
        <CalendarTwoTone />
        {data.startDate}
      </span>
      <span className="indicator">
        <ClockCircleTwoTone />
        {data.startTime}<span>-{data.endTime}</span>
      </span>

      {isview && (
        <span
          className="card-click"
          onClick={() => {
            myViewcard(data);
          }}
        >
          <EyeTwoTone />
         <p>view</p> 
        </span>
      )}
      {isedit && (
        <span className="card-click" onClick={()=>handleNavigate(data)}>
          <EditTwoTone />
          <p> edit</p>
        </span>
      )}
      {divison === "History" ? <span className={data.status==="COMPLETED"?"positive":"negative"}>{data.status}</span> : <></>}

      {iscancel && (
        <Rejectbutton
          btnname={divison === "Pending" ? "Withdraw" : "Cancel"}

          callback={() => widthdrawcall(data.id as number)}
          positive={false}
        />
      )}
    </div>
  );
};
export default Card;
