import { ChangeEvent, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button,DatePicker,Form,Input,InputNumber,Select,TimePicker,  Checkbox} from 'antd';
import { Modal } from 'antd';
import dayjs,{ Dayjs } from 'dayjs';
import {formatDateToISO,formatTimeTo24Hours} from '../../utils/function'
import { icons } from '.././../constant/reacticonConstant/index';
import { RoomData, EmploeenameidType } from '../../type';
import 'dayjs/locale/en';

//css properties for model
import {searchBoxStyle,selectionBoxStyle,sbHeaderStyle
       ,sbItemBoxStyle,sbListStyle,employeeBoxStyle,ebHeaderStyle,ebItemBoxStyle,checkboxItemBoxStyle,
       profileIconStyle,bookingstatus_box,bb_info,button,attendees_button} from './cssproperties';

//stylesheet
import './index.scss';

const Roomform = ({roomDetail,category,empnameid,callbackbody,cofirmbooking,editdata,dateincard}: RoomData) => {
 const guestEmails: string[] = editdata.guestList.map((guest: any) => {
  if (typeof guest === 'string') {
    return guest; 
  } else {
    return guest.email; 
  }
});
const navigate=useNavigate();
const startTimeValue = editdata.startTime ? dayjs(editdata.startTime, "h:mm a") : null;
const endTimeValue = editdata.endTime ? dayjs(editdata.endTime, "h:mm a") : null;
const initialDateValue = editdata.startDate ? dayjs(editdata.startDate, "DD-MM-YYYY") : null;

const { Option } = Select;
const [timebool, settimeboo] = useState({
  starttimebool: false,
  endtimebool: false,
  datebool: false
});

const updatetimdateestatus = (key:any, value:boolean) => {
  settimeboo(prevState => ({
    ...prevState,
    [key]: value  
  }));
};

  const [meetingtype, setmeetingtype] = useState<string>(editdata.meetingCategory);
  const [description, setdescription] = useState<string>(editdata.description);
  const [noofattendees, setnoofattendees] = useState(0);
  const [meetingname, setmeetingname] = useState<string>(editdata.meetingName);
  const [isconfiembookingModal, setIsconfirmbookingModal] = useState(false);
  const [isFormFilled, setIsFormFilled] = useState<string | boolean | undefined>(false);
  const [isattendeesModal, setattendeesModal] = useState(false);
  const [searchvalue, setsearchvalue] = useState<string>('');
  const [meetingnameLength, setMeetingnameLength] = useState(0);
  const [emaildata, setemaildata] = useState(guestEmails);

  const [date, setdate] = useState<string>();
  const [starttime, setstarttime] = useState<string>();
  const [endtime, setendtime] = useState<string>();

  const [datevalue, setdatevalue] = useState<Dayjs | null>(initialDateValue);
  const [starttimevalue,setstarttimevalue]=useState<Dayjs | null>(startTimeValue);
  const [endtimevalue,setendtimevalue]=useState<Dayjs | null>(endTimeValue);

  const startdatetime = date + 'T' + starttime;
  const enddatetime = date + 'T' + endtime;

  const handleEmployeeModel = () => {
    setattendeesModal(true);
  };

  const handlecheck = (email: string) => {
    if (emaildata.includes(email)) {
      setemaildata(emaildata.filter((e) => e !== email));
    } else {
      setemaildata([...emaildata, email]);
    }
  };

  const filtereddata =
    Array.isArray(empnameid) &&
    empnameid.filter((n) =>
      n.name.toLowerCase().includes(searchvalue.toLowerCase())
    );

  const showModal = () => {
    setIsconfirmbookingModal(true);
  };

  const handleOk = () => {
    setattendeesModal(false);
  };

  const handleCancel = () => {
    setIsconfirmbookingModal(false);
    setattendeesModal(false);
  };

  const onDateChange = (date: any) => {
    if (date) {
       setdatevalue(date);
       setdate(formatDateToISO(date));
       updatetimdateestatus('dtbool', true)
    }
  };

  const onStartChange = (time: Dayjs) => {
    if (time) {
      setstarttimevalue(time);
      setstarttime(formatTimeTo24Hours(time));
      updatetimdateestatus('stbool', true)

    }
  };

  const onEndChange = (time: Dayjs) => {
    if (time) {
      setendtimevalue(time);
      setendtime(formatTimeTo24Hours(time));
      updatetimdateestatus('etbool', true)
    }
  };

  const handleMeetingnameChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value.length > 0 && meetingnameLength <= 25) {
      setmeetingname(value);
      setMeetingnameLength(value.length);
    } else {
      setmeetingname('');
    }
  };

  const requestBody = {
    meetingName: meetingname,
    meetingCategory: meetingtype,
    userEntityList: emaildata,
    description: description,
    startTime: startdatetime,
    endTime: enddatetime,
    roomEntityId: roomDetail?.id,
  };

  useEffect(() => {
    if (dateincard ){
      setdatevalue(dayjs(dateincard.$d));
      setdate(formatDateToISO(dayjs(dateincard.$d)));
      updatetimdateestatus('dtbool',true);
    }   
    if (startTimeValue && timebool.starttimebool===false) {
      setstarttime(formatTimeTo24Hours(startTimeValue));
    }
    if (endTimeValue && timebool.endtimebool===false) {
      setendtime(formatTimeTo24Hours(endTimeValue));
    }
    if (initialDateValue && timebool.datebool===false) {
      setdate(formatDateToISO(initialDateValue));
    }

    setnoofattendees(emaildata.length);
    const isFormValid =
      meetingtype &&
      date&&
      starttime &&
      endtime &&
      emaildata.length > 0 &&
      description &&
      meetingname;
    setIsFormFilled(isFormValid);

  // eslint-disable-next-line
}, [meetingtype, date,starttime, endtime,emaildata,description,meetingname,timebool, endTimeValue, initialDateValue, startTimeValue]);

  const handleform = () => {
    callbackbody(requestBody);
    showModal();
    setmeetingname('');
    setmeetingtype('');
    setemaildata([]);
    setdescription('');
    setdatevalue(null);
    setstarttimevalue(null);
    setendtimevalue(null);
  };
  const handlenavigatetocard=()=>{
    setIsconfirmbookingModal(false);
    navigate('/user/booking');
  }
  // eslint-disable-next-line
  const [currentDate, setCurrentDate] = useState(dayjs());
  // eslint-disable-next-line
  const [currentTime, setCurrentTime] = useState(dayjs());
  return (
    <Form className='BookDetail_form' layout={'vertical'} onFinish={handleform}>
      <h1>{roomDetail?.name}</h1>
      <div className='form_item_row1'>
        <Form.Item label='Meeting Category'>
          <Select className='category_select'
            placeholder='Select Meeting Type' value={meetingtype}
            onChange={(selectedValue) => setmeetingtype(selectedValue)}
            allowClear
          >
            {Array.isArray(category) &&
              category?.map((meetingType: string, index: number) => (
                <Option key={index} value={meetingType}>
                  {meetingType}
                </Option>
              ))}
          </Select>
        </Form.Item>
        <Form.Item label='Date'>
        <DatePicker className='date'value={datevalue} onChange={onDateChange} disabledDate={(current) => current && current < currentDate.startOf('day')} disabled={dateincard}/>
        </Form.Item>
      </div>
      <div className='form_item_row2'>
        <Form.Item label='Meeting Name'>  
          <Input
           className='meetname'
            placeholder='Enter Meeting Name'
            value={meetingname}
            onChange={handleMeetingnameChange}
          />
        </Form.Item>
        <Form.Item label='Attendees'>
          <InputNumber
            className='attende_input'
            onChange={handleEmployeeModel}
            value={noofattendees}
          />
        </Form.Item>
        {isattendeesModal && (
          <Modal
            open={isattendeesModal}
            onOk={handleOk}
            onCancel={handleCancel}
            width={450}
            footer={[
              <div style={attendees_button}>
                <Button key='ok' type='primary' onClick={handleOk}> Confirm</Button>
              </div>,
            ]}
          >
            <Input style={searchBoxStyle} placeholder='Search ....' value={searchvalue} onChange={(e) => setsearchvalue(e.target.value)} />
            {noofattendees > 0 && (
              <div style={selectionBoxStyle}>
                <h2 style={sbHeaderStyle}>Selected</h2>
                <div style={sbItemBoxStyle}>
                  {emaildata.map((email: string, index: number) => (
                    <div key={index}>
                      <Checkbox checked onChange={() => handlecheck(email)}>
                        <div style={sbListStyle}>
                          <icons.IoPersonCircleSharp style={profileIconStyle} />
                          <p style={{ alignSelf: 'center' }}>{email}</p>
                        </div>
                      </Checkbox>
                    </div>
                  ))}
                </div>
              </div>
            )}
            <div style={employeeBoxStyle}>
              <h2 style={ebHeaderStyle}>Employee List</h2>
              <div style={ebItemBoxStyle}>
                {Array.isArray(filtereddata) &&
                  filtereddata?.map((emp: EmploeenameidType, index: number) => (
                    <Checkbox key={index} onChange={(e) => handlecheck(emp?.email)}>
                      <div style={checkboxItemBoxStyle}>
                        <icons.IoPersonCircleSharp style={profileIconStyle} />
                        <div>
                          <p>{emp?.name}</p>
                          <p>{emp?.email}</p>
                        </div>
                      </div>
                    </Checkbox>
                  ))}
              </div>
            </div>
          </Modal>
        )}
      </div>
      <Form.Item className='form_item_row3' label='Agenda'>
        <Input.TextArea rows={5} value={description} onChange={(e) => setdescription(e.target.value)} />
      </Form.Item>
      <div className='form_item_row4'>
        <Form.Item label='Start Time'>
          <TimePicker className='time' format='h:mm a'value={starttimevalue}onChange={onStartChange} disabledHours={() => (currentTime && currentDate.isSame(datevalue, 'day')) ? Array.from({ length: currentTime.hour() }, (_, i) => i) : []}/>        
          </Form.Item>
        <Form.Item label='End Time'>
        <TimePicker className='time'format='h:mm a'value={endtimevalue} onChange={onEndChange} disabledHours={() => (currentTime && currentDate.isSame(datevalue, 'day')) ? Array.from({ length: currentTime.hour() }, (_, i) => i) : []}/>
        </Form.Item>
        <Button type='primary' htmlType='submit' disabled={!isFormFilled}>
          Book Now
        </Button>
        <Modal open={isconfiembookingModal} onOk={handleOk} centered onCancel={handleCancel} footer={null}>
          <div style={bookingstatus_box}>
            {cofirmbooking?.icon}
            <p>{cofirmbooking?.shortdes}</p>
            <p style={bb_info}>{cofirmbooking?.longdes}</p>
            { cofirmbooking?.shortdes==='Loading..'?<></>:
             <button style={button} onClick={handlenavigatetocard}>OK</button>
            }
          </div>
        </Modal>
      </div>
    </Form>
  );
};

export default Roomform;
