import { ChangeEvent, useState, useEffect } from 'react';
import { Button,DatePicker,Form,Input,InputNumber,Select,TimePicker,  Checkbox} from 'antd';
import { Modal } from 'antd';
import  { Dayjs } from 'dayjs';
import moment from 'moment';
import { icons } from '.././../constant/reacticonConstant/index';
import { RoomData, EmploeenameidType } from '../../type';

//cssproperties for model
import {searchBoxStyle,selectionBoxStyle,sbHeaderStyle
       ,sbItemBoxStyle,sbListStyle,employeeBoxStyle,ebHeaderStyle,ebItemBoxStyle,checkboxItemBoxStyle,
       profileIconStyle,bookingstatus_box,bb_info,button,attendees_button} from './cssproperties';

//stylesheet
import './index.scss';

const Roomform = ({roomData,category,empnameid,callbackbody,cons}: RoomData) => {

  const [meetingtype, setmeetingtype] = useState<string>('');
  const [date, setdate] = useState<string>();
  const [description, setdescription] = useState<string>();
  const [starttime, setstarttime] = useState<string>();
  const [endtime, setendtime] = useState<string>();
  const [datevalue,setdatevalue]=useState<Dayjs | null>(null);
  const [starttimevalue,setstarttimevalue]=useState<Dayjs | null>(null);
  const [endtimevalue,setendtimevalue]=useState<Dayjs | null>(null);
  const startdatetime = date + 'T' + starttime;
  const enddatetime = date + 'T' + endtime;
  const [emaildata, setemaildata] = useState([] as string[]);
  const [noofattendees, setnoofattendees] = useState(0);
  const [meetingname, setmeetingname] = useState<string>();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const { Option } = Select;
  const [isFormFilled, setIsFormFilled] = useState<string | boolean | undefined>(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [searchvalue, setsearchvalue] = useState<string>('');
  const [meetingnameLength, setMeetingnameLength] = useState(0);

  const handleEmployeeModel = () => {
    setIsModalVisible(true);
  };

  const handlecheck = (email: string) => {
    if (emaildata.includes(email)) {
      setemaildata(emaildata.filter((e) => e !== email));
    } else {
      setemaildata([...emaildata, email]);
    }
  };

  const filtereddata =
    Array.isArray(empnameid) &&
    empnameid.filter((n) =>
      n.name.toLowerCase().includes(searchvalue.toLowerCase())
    );

  const showModal = () => {
    setIsModalOpen(true);
  };

  const handleOk = () => {
    setIsModalOpen(false);
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    setIsModalVisible(false);
  };

  const disabledDate = (current: any) => {
    return current && current < moment().startOf('day');
  };

  const onDateChange = (date: any, dateString: any) => {
    if (date) {
      setdatevalue(date);
      setdate(date.format('YYYY-MM-DD'));
    }
  };

  const onStartChange = (time: Dayjs, timeString: any) => {
    if (time) {
      setstarttimevalue(time);
      setstarttime(time.format('hh:mm:ss'));
    }
  };

  const onEndChange = (time: Dayjs, timeString: any) => {
    if (time) {
      setendtimevalue(time);
      setendtime(time.format('hh:mm:ss'));
    }
  };

  const handleMeetingnameChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    if (value.length > 0 && meetingnameLength <= 25) {
      setmeetingname(value);
      setMeetingnameLength(value.length);
    } else {
      setmeetingname('');
    }
  };

  const requestBody = {
    meetingName: meetingname,
    meetingCategory: meetingtype,
    userEntityList: emaildata,
    description: description,
    startTime: startdatetime,
    endTime: enddatetime,
    roomEntityId: roomData?.id,
  };

  useEffect(() => {
    setnoofattendees(emaildata.length);
    const isFormValid =
      meetingtype &&
      date &&
      starttime &&
      endtime &&
      emaildata.length > 0 &&
      description &&
      meetingname;
    setIsFormFilled(isFormValid);
  }, [
    meetingtype,
    date,
    starttime,
    endtime,
    emaildata,
    description,
    meetingname,
  ]);

  const handleform = () => {
    callbackbody(requestBody);
    showModal();
    setmeetingname('');
    setmeetingtype('');
    setemaildata([]);
    setdescription('');
    setdatevalue(null);
    setstarttimevalue(null);
    setendtimevalue(null);
  };

  return (
    <Form className='BookDetail_form' layout={'vertical'} onFinish={handleform}>
      <h1>{roomData?.name}</h1>
      <div className='form_item_row1'>
        <Form.Item label='Meeting Category'>
          <Select className='category_select'
            placeholder='Select Meeting Type' value={meetingtype}
            onChange={(selectedValue) => setmeetingtype(selectedValue)}
            allowClear
          >
            {Array.isArray(category) &&
              category?.map((meetingType: string, index: number) => (
                <Option key={index} value={meetingType}>
                  {meetingType}
                </Option>
              ))}
          </Select>
        </Form.Item>
        <Form.Item label='Date'>
          <DatePicker className='date' value={datevalue} onChange={onDateChange} disabledDate={disabledDate} />
        </Form.Item>
      </div>
      <div className='form_item_row2'>
        <Form.Item label='Meeting Name'>  
          <Input
           className='meetname'
            placeholder='Enter Meeting Name'
            value={meetingname}
            onChange={handleMeetingnameChange}
          />
        </Form.Item>
        <Form.Item label='Attendees'>
          <InputNumber
            className='attende_input'
            onChange={handleEmployeeModel}
            value={noofattendees}
          />
        </Form.Item>
        {isModalVisible && (
          <Modal
            open={isModalVisible}
            onOk={handleOk}
            onCancel={handleCancel}
            width={450}
            footer={[
              <div style={attendees_button}>
                <Button key='ok' type='primary' onClick={handleOk}> Confirm</Button>
              </div>,
            ]}
          >
            <Input style={searchBoxStyle} placeholder='Search ....' value={searchvalue} onChange={(e) => setsearchvalue(e.target.value)} />
            {noofattendees > 0 && (
              <div style={selectionBoxStyle}>
                <h2 style={sbHeaderStyle}>Selected</h2>
                <div style={sbItemBoxStyle}>
                  {emaildata.map((email: string, index: number) => (
                    <div key={index}>
                      <Checkbox checked onChange={() => handlecheck(email)}>
                        <div style={sbListStyle}>
                          <icons.IoPersonCircleSharp style={profileIconStyle} />
                          <p style={{ alignSelf: 'center' }}>{email}</p>
                        </div>
                      </Checkbox>
                    </div>
                  ))}
                </div>
              </div>
            )}
            <div style={employeeBoxStyle}>
              <h2 style={ebHeaderStyle}>Employee List</h2>
              <div style={ebItemBoxStyle}>
                {Array.isArray(filtereddata) &&
                  filtereddata?.map((emp: EmploeenameidType, index: number) => (
                    <Checkbox key={index} onChange={(e) => handlecheck(emp?.email)}>
                      <div style={checkboxItemBoxStyle}>
                        <icons.IoPersonCircleSharp style={profileIconStyle} />
                        <div>
                          <p>{emp?.name}</p>
                          <p>{emp?.email}</p>
                        </div>
                      </div>
                    </Checkbox>
                  ))}
              </div>
            </div>
          </Modal>
        )}
      </div>
      <Form.Item className='form_item_row3' label='Agenda'>
        <Input.TextArea value={description} onChange={(e) => setdescription(e.target.value)} />
      </Form.Item>
      <div className='form_item_row4'>
        <Form.Item label='Start Time'>
          <TimePicker format='h:mm A' value={starttimevalue} onChange={onStartChange} />
        </Form.Item>
        <Form.Item label='End Time'>
          <TimePicker format='h:mm A' value={endtimevalue} onChange={onEndChange} />
        </Form.Item>
        <Button type='primary' htmlType='submit' disabled={!isFormFilled}>
          Book Now
        </Button>
        <Modal open={isModalOpen} onOk={handleOk} centered onCancel={handleCancel} footer={null}>
          <div style={bookingstatus_box}>
            {cons?.icon}
            <p>{cons?.shortdes}</p>
            <p style={bb_info}>{cons?.longdes}</p>
            <button style={button} onClick={handleOk}>OK</button>
          </div>
        </Modal>
      </div>
    </Form>
  );
};

export default Roomform;
