import { FaUserCircle } from "react-icons/fa";
import "./index.scss";
import { FeedbackInfoCard } from "../../type";
interface Cardtype {
  data: FeedbackInfoCard;
  active:string;
  index:number;
  callback:(data:FeedbackInfoCard,index:number)=> void;
}

const FeedbackInfo = (props: Cardtype) => {
  const { data ,active,callback,index} = props;

  const lastseen = new Date(data.timestamp);
  const currentDate = new Date();

  const timeDifference = currentDate.getTime() - lastseen.getTime();

  const seconds = Math.floor(timeDifference / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);
  const days = Math.floor(hours / 24);

  let timeElapsedString = "";
  if (days > 0) {
    timeElapsedString = `${days} day ago`;
  } else if (hours > 0) {
    timeElapsedString = `${hours} hrs ago`;
  } else if (minutes > 0) {
    timeElapsedString = `${minutes} min ago`;
  } else {
    timeElapsedString = `${seconds} sec ago`;
  }

  return (
    <div className={active} onClick={()=>{ if(active==="info"){
      callback(data,index)
        }
      }}>
      <section className="name-details">
        <FaUserCircle className="avatar"/>
        <h4>
          {data.hostname} <span>{timeElapsedString}</span>
        </h4>
      </section>
      <p>
        {data.feedback}
        {data.rating}
      </p>
    </div>
  );
};
export default FeedbackInfo;
